import { NextPage, GetStaticPaths, GetStaticProps } from "next";
import {
  getSortedPostsData,
  getAllPostSlugs,
  getPostData,
  getCategories,
  getTags,
} from "../../lib/posts";
import Layout from "../../components/Layout";
import PostAttr from "../../components/PostAttr";
import Breadcrumb from "../../components/Breadcrumb";
import Sidebar from "../../components/Sidebar";

const Post: NextPage = ({ postData, allPostData, categories, tags }) => {
  return (
    <Layout
      pageTitle="Home"
      pageDescription="Home"
      categories={categories}
      tags={tags}
    >
      <div className="l-show">
        <div className="l-show__main">
          <Breadcrumb
            links={
              postData.category && [
                {
                  href: `/category/${postData.category}`,
                  title: postData.category,
                },
              ]
            }
          />
          <article className="article">
            <figure className="article__eye-catch">
              <img src={`/assets/${postData.eye_catch}`} alt={postData.title} />
            </figure>

            <div className="article__header">
              <span className="article__pub-date">
                公開日：{postData.pub_date}
                {postData.mod_date && `更新日：${postData.mod_date}`}
              </span>
            </div>
            <h1 className="article__title">{postData.title}</h1>
            <PostAttr
              className="article__attr"
              category={postData.category || null}
              tags={postData.tags || null}
            />
            <hr className="overPC" />
            <div className="toc"></div>
            <div className="article__body">
              <div dangerouslySetInnerHTML={{ __html: postData.contentHtml }} />
            </div>
          </article>
        </div>
        <Sidebar allPostData={allPostData} />
      </div>
    </Layout>    
  )
}

export default Post;

