export const getStaticProps: GetStaticProps = async ({ params }) => {
  const postData = await getPostData(params.slug);
  const allPostData = getSortedPostsData();
  const categories = getCategories();
  const tags = getTags();
  return {
    props: {
      postData,
      allPostData,
      categories,
      tags,
    },
  };
}

