import Layout from "../../components/Layout";
import PostList from "../../components/PostList";
import Breadcrumb from "../../components/Breadcrumb";
import {
  getCategoryPaths,
  getSortedCategoryPostsData,
  getCategories,
  getTags,
} from "../../lib/posts";

export default function CategoryIndex({
  category,
  categoryPostsData,
  categories,
  tags,
}) {
  return (
    <Layout
      pageTitle="Home"
      pageDescription="Home"
      categories={categories}
      tags={tags}
    >
      <section className="l-index">
        <Breadcrumb
          links={[
            {
              href: `/category/${category}`,
              title: category,
            },
          ]}
        />

        <div className="index__title">{category}の記事一覧</div>
        <PostList allPostData={categoryPostsData} />
      </section>
    </Layout>
  );
}

export async function getStaticProps({ params }) {
  const category = params.category;
  const categoryPostsData = getSortedCategoryPostsData(category);
  const categories = getCategories();
  const tags = getTags();
  return {
    props: {
      category,
      categoryPostsData,
      categories,
      tags,
    },
  };
}

export async function getStaticPaths() {
  const paths = getCategoryPaths();
  return {
    paths,
    fallback: false,
  };
}
